INSERT INTO `rp_report_sql` VALUES ('1', 'select count(1)   from (select date_format(create_time, \'%Y-%m-%d\') create_time,                id,                p_id,                name,                resource_action           from uc_resource          where create_time > str_to_date(:begintime, \'%Y-%m-%d\')            and create_time < str_to_date(:endtime, \'%Y-%m-%d\') + 1 ) vvv', 'select date_format(create_time, \'%Y-%m-%d %T\') create_time,        id,        p_id,        name,        resource_action   from uc_resource   where create_time > str_to_date(:begintime, \'%Y-%m-%d\')    and create_time < str_to_date(:endtime, \'%Y-%m-%d\') + 1   order by id desc', '柱状图1SQL', null, 'ods'), ('2', ' select count(1) from (select date_format(create_time, \'%Y-%m-%d %T\') create_time,        id,        p_id,        name,        resource_action   from uc_resource   where create_time > str_to_date(:begintime, \'%Y-%m-%d\')    and create_time < str_to_date(:endtime, \'%Y-%m-%d\') + 1   ) vvv', 'select date_format(create_time, \'%Y-%m-%d %T\') create_time,        id,        p_id,        name,        resource_action   from uc_resource   where create_time > str_to_date(:begintime, \'%Y-%m-%d\')    and create_time < str_to_date(:endtime, \'%Y-%m-%d\') + 1   order by id desc', '饼状图测试SQL', null, 'ods'), ('3', 'select count(1) from (select date_format(create_time, \'%Y-%m-%d %T\') create_time,        id,        p_id,        name,        resource_action   from uc_resource   where create_time > str_to_date(:begintime, \'%Y-%m-%d\')    and create_time < str_to_date(:endtime, \'%Y-%m-%d\') + 1  ) vvv', 'select date_format(create_time, \'%Y-%m-%d %T\') create_time,        id,        p_id,        name,        resource_action   from uc_resource   where create_time > str_to_date(:begintime, \'%Y-%m-%d\')    and create_time < str_to_date(:endtime, \'%Y-%m-%d\') + 1   order by id desc', '二级表头测试SQL', null, 'ods'), ('4', ' select count(1) from (select date_format(create_time, \'%Y-%m-%d %T\') c