ource WHERE 	create_time > str_to_date(:begintime, \'%Y-%m-%d\') AND create_time < str_to_date(:endtime, \'%Y-%m-%d\') + 1 order BY 	id DESC', 'static2统计SQL', null, 'ods'), ('8', 'select count(1)   from (select fid, fup, type, name, status           from gs_forum_forum          where type = :status) vvv', 'select fid, fup, type, name, status   from gs_forum_forum  where type = :status  order by fid', '多数据源测试SQL', null, 'pos'), ('9', ' select count(1) from (select date_format(create_time, \'%Y-%m-%d %T\') create_time,        id,        p_id,        name,        resource_action   from uc_resource   where create_time > str_to_date(:begintime, \'%Y-%m-%d\')    and create_time < str_to_date(:endtime, \'%Y-%m-%d\') + 1   ) vvv', 'select date_format(create_time, \'%Y-%m-%d %T\') create_time,        id,        p_id,        name,        resource_action   from uc_resource   where create_time > str_to_date(:begintime, \'%Y-%m-%d\')    and create_time < str_to_date(:endtime, \'%Y-%m-%d\') + 1   order by id desc', '多图展示SQL', null, 'ods'), ('10', ' select count(1) from (select date_format(create_time, \'%Y-%m-%d %T\') create_time,       id,       p_id,       name,       resource_action  from uc_resource where p_id = :pid ) vvv', 'select date_format(create_time, \'%Y-%m-%d %T\') create_time,       id,       p_id,       name,       resource_action  from uc_resource where p_id = :pid order by id desc', '自定义值条件设置SQL', null, 'ods'), ('11', ' select count(1) from (select date_format(create_time, \'%Y-%m-%d %T\') create_time,       id,       p_id,       name,       resource_action  from uc_resource where p_id = :pid ) vvv', 'select date_format(create_time, \'%Y-%m-%d %T\') create_time,       id,       p_id,       name,       resource_action  from uc_resource where p_id = :pid order by id desc', '根据SQL获取值条件设置SQL', null, 'ods'), ('12', ' select count(1) from (select date_format(create_time, \'%Y-%m-%d %T\') create_time,       id,       p_id,       name,       resource_action  from uc_resource where p_id = :pid ) vvv', 'select date_format(create_time, \'%Y-%m-%d %T\') create_time,       id,       p_id,       name,       resource_action  from uc_resource where p_id = :pid order by id desc', '模糊查询条件SQL', null, 'ods'), ('13', ' select count(1) from (select date_format(create_time, \'%Y-%m-%d %T\') create_time,       id,       p_id,       name,       resource_action  from uc_resource where p_id in (:pid) ) vvv', 'select date_format(create_time, \'%Y-%m-%d %T\') create_time,       id,       p_id,       name,       resource_action  from uc_resource where p_id in (:pid) order by id desc', '多选条件设置SQL', null, 'ods'), ('14', 'smtp.163.com', 'smtp.163.com', '邮箱HOST', null, 'ods'), ('15', '', '', '邮箱USERNAME', null, 'ods'), ('16', '', '', '邮箱密码', null, 'ods'), ('17', '/mydata/report/tmp', '/mydata/report/tmp', '邮箱图片临时存放目录', null, 'ods'), ('18', '13927470069@163.com', '13927470069@163.com', '邮箱FROM', null, 'ods'), ('19', '25', '25', '邮箱端口', null, 'ods'), ('20', 'select count(1) from (select 	rp_date, 	user_name, 	ope_action, 	rptname, 	ope_id, 	waste_time, 	ip, 	exception from 	( 		select 			date_format( 				rl.create_time, 				\'%Y-%m-%d %T\' 			) rp_date, 			rl.user_name, 			rl.ope_action, 			rl.ip, 			ifnull(rr.rptname,rl.ope_id) rptname, 			rl.ope_id ope_id, 			CASE rl.waste_time 		WHEN \'0\' THEN 			\'异常\' 		ELSE 			waste_time || \'毫秒\' 		END waste_time, 		rl.exception 	from 		(select * from rptlog) rl     left join rp_report_sql rr 	on rl.ope_id = trim(rr.sql_id) 	where rl.user_name <> \'perAdmin\'   and rl.create_time >= str_to_date(:begintime, \'%Y-%m-%d\') 	and rl.create_time < (str_to_date(:endtime, \'%Y-%m-%d\') + 1) 	) t where 	1 = 1 and user_name = (:userName) and ope_id = (:sqlId) )vv', 'select 	rp_date, 	user_name, 	ope_action, 	rptname, 	ope_id, 	waste_time, 	ip, 	exception from 	( 		select 			date_format( 				rl.create_time, 				\'%Y-%m-%d %T\' 			) rp_date, 			rl.user_name, 			rl.ope_action, 			rl.ip, 			ifnull(rr.rptname,rl.ope_id) rptname, 			rl.ope_id ope_id, 			CASE rl.waste_time 		WHEN \'0\' THEN 			\'异常\' 		ELSE 			waste_time || \'毫秒\' 		END waste_time, 		rl.exception 	from 		(select * from rptlog) rl     left join rp_report_sql rr 	on rl.ope_id = trim(rr.sql_id) 	where rl.user_name <> \'perAdmin\'   and rl.create_time >= str_to_date(:begintime, \'%Y-%m-%d\') 	and rl.create_time < (str_to_date(:endtime, \'%Y-%m-%d\') + 1) 	) t where 	1 = 1 and user_name = (:userName) and ope_id = (:sqlId) order by 	1 desc', '报表日志明细查询SQL', null, 'ods'), ('21', ' select count(1) from (select command_name,command_txt from linux_command where command_name like IF(CONCAT(\'%\',:command_name,\'%\')=\'% %\', \'%%\', CONCAT(\'%\',:command_name,\'%\')) ) vvv', 'select command_name,command_txt from linux_command where command_name like IF(CONCAT(\'%\',:command_name,\'%\')=\'% %\', \'%%\', CONCAT(\'%\',:command_name,\'%\')) order by id\n\n', 'linux常用命令SQL', null, 'ods');
COMMIT;

-- ----------------------------
--  Table structure for `rptchart`
-- ----------------------------
DROP TABLE IF EXISTS `rptchart`;
CREATE TABLE `rptchart` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `toolflag` varchar(255) DEFAULT NULL COMMENT '报表标志',
  `chart_name` varchar(100) DEFAULT NULL COMMENT '图形名称',
  `chart_type` varchar(10) DEFAULT NULL COMMENT '图形类型',
  `datavsle` varchar(2000) DEFAULT NULL COMMENT 'legend数据列',
  `datavsx` varchar(50) DEFAULT NULL COMMENT 'x轴数据列',
  `chart_option` text COMMENT '图形option',
  `show_rownum` tinyint(4) DEFAULT NULL COMMENT '展示数据数目',
  `chart_order` tinyint(4) DEFAULT NULL COMMENT '图形排序',
  `create_time` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` datetime DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '修改时间',
  PRIMARY KEY (`id`),
  KEY `rptchart_toolflag` (`toolflag`)
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8 COMMENT='报表图形表';

