DROP TABLE IF EXISTS `rptlog`;
CREATE TABLE `rptlog` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `user_name` varchar(50) DEFAULT NULL COMMENT '用户名',
  `ope_action` varchar(50) DEFAULT NULL COMMENT '操作动作',
  `ope_id` varchar(200) DEFAULT NULL COMMENT '执行的SQLID或者toolFlag',
  `waste_time` varchar(10) DEFAULT NULL COMMENT '消耗时间',
  `ip` varchar(20) DEFAULT NULL COMMENT 'ip',
  `exception` text COMMENT '异常信息',
  `create_time` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  PRIMARY KEY (`id`),
  KEY `rptlog_action` (`ope_action`)
) ENGINE=InnoDB AUTO_INCREMENT=6449 DEFAULT CHARSET=utf8 COMMENT='报表操作日志';

