  `old_id` varchar(64) DEFAULT NULL,
  `creater_id` bigint(20) DEFAULT NULL,
  `modifier_id` bigint(20) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=7789 DEFAULT CHARSET=utf8;

